// https://github.com/Lora-net/packet_forwarder/blob/master/PROTOCOL.TXT

function reverse (src) {
  var buffer = Buffer.allocUnsafe(src.length)
  for (var i = 0, j = src.length - 1; i <= j; ++i, --j) {
    buffer[i] = src[j]
      buffer[j] = src[i]
  }

  return buffer
}


fullMessage = msg.payload;
fullMessage = fullMessage.slice(fullMessage.indexOf('{'));
message = JSON.parse(fullMessage);

//head_raw=msg.payload.substring(0,12);
//head = Buffer.from(head_raw,'binary').toString("hex");

gwmac_raw=msg.payload.substring(4,12);
gwmac = Buffer.from(gwmac_raw,'binary').toString("hex");

if (message.rxpk && message.rxpk.length > 0 && message.rxpk[0].data) {
    var payloadBase64 = message.rxpk[0].data;
    var bufferDevice = Buffer.from(payloadBase64, 'base64').slice(1,5);
    bufferDevice = reverse(bufferDevice);
    message.rxpk[0].DevAddr = bufferDevice.toString('hex').toUpperCase();

    var frameCounter = Buffer.from(payloadBase64, 'base64').slice(6,8);
    frameCounter = reverse(frameCounter);
    message.rxpk[0].FrameCounter = parseInt(frameCounter.toString('hex'), 16);
}

msg.payload = message;
msg.payload.gwmac = gwmac;
//msg.payload.head = head;
return msg;

// Filter if no received packet (e.g. status)
if (!msg.payload.rxpk) {
    return;
}

// Extract all information
DevAddr = msg.payload.rxpk[0].DevAddr;
frameCounter = msg.payload.rxpk[0].FrameCounter;
rawData = msg.payload.rxpk[0].data;

DataRate = msg.payload.rxpk[0].datr;
SNR = msg.payload.rxpk[0].lsnr;
RSSI = msg.payload.rxpk[0].rssi;
frequency = msg.payload.rxpk[0].freq;
codingRate = msg.payload.rxpk[0].codr;
