try {
  network = msg.network.operator
} catch(err) {
  network = "(unknown)"
}
// Change gateway id upon udp source ip
if (msg.ip === "1.2.3.4") {
    GatewayId = "gateway001";
} else if (msg.ip === "1.2.3.5") {
    GatewayId = "gateway002";
} else {
    // Use ip including port
    GatewayId = msg.fromip;
}

// Prepare for fields and tags for influxdb
msg.payload = [{
    rawData: rawData,
    SNR: SNR,
    RSSI: RSSI,
    frequency: frequency,
    codingRate: codingRate,
    frameCounter: frameCounter,
    airtime: airtime
},
{
    DevAddr: DevAddr,
    GatewayId: GatewayId,
    DataRate: DataRate,
    Network: network
}];
return msg;

